package com.article.controller;import java.util.LinkedHashMap;import java.util.List;import java.util.Map;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import com.article.entity.manager.ManagerUser;import com.article.service.manager.ManagerUserService;import net.sf.json.JSONArray;import net.sf.json.JSONObject;@Controller@RequestMapping("managerUser")public class ManagerUserController {	@Autowired	private ManagerUserService managerUserService;	@RequestMapping(value = "listPageManagerUser", method = RequestMethod.GET, produces = "text/html;charset=UTF-8")	@ResponseBody	public String listPageManagerUser(ManagerUser managerUser) {		Map<Object,Object> maps = new LinkedHashMap<>();		Map<Object,Object> map = new LinkedHashMap<>();		List<ManagerUser> byManagerUser = managerUserService.listPageManagerUser(managerUser);		JSONArray jsonArray = JSONArray.fromObject(byManagerUser);		map.put("list", jsonArray);		maps.put("list", map);		JSONObject fromObject = JSONObject.fromObject(maps);		return fromObject.toString();	}	@RequestMapping(value = "getByManagerUser", method = RequestMethod.POST, produces = "text/html;charset=UTF-8")	@ResponseBody	public String getByManagerUser(ManagerUser managerUser) {		ManagerUser byManagerUser = managerUserService.getByManagerUser(managerUser);		JSONObject fromObject = JSONObject.fromObject(byManagerUser);		return fromObject.toString();	}	@RequestMapping(value = "insertManagerUser", method = RequestMethod.POST, produces = "text/html;charset=UTF-8")	@ResponseBody	public int insertManagerUser(ManagerUser managerUser) {		return managerUserService.insertManagerUser(managerUser);	}	@RequestMapping(value = "deleteManagerUser", method = RequestMethod.POST, produces = "text/html;charset=UTF-8")	@ResponseBody	public int deleteManagerUser(String id) {		return managerUserService.deleteManagerUser(id);	}	@RequestMapping(value = "updateManagerUser", method = RequestMethod.POST, produces = "text/html;charset=UTF-8")	@ResponseBody	public int updateManagerUser(ManagerUser managerUser) {		return managerUserService.updateManagerUser(managerUser);	}}